mapper.update:
UPDATE HEART
SET heart_count = heart_count + 1
WHERE d_article_id = #{dailyArticleId}
AND member_id = #{memberId}

UPDATE HEART
SET heart_count = heart_count - 1
WHERE d_article_id = #{dailyArticleId}
AND member_id = #{memberId}

mapper.select:
SELECT COALESCE(SUM(heart_count), 0) AS heart_count
FROM heart
WHERE d_article_id = #{dailyArticleId}
AND member_id = #{memberId}

SELECT SUM(heart_count)
FROM heart
WHERE d_article_id = #{dailyArticleId}

SELECT NVL(COUNT(*), 0)
FROM heart
WHERE d_article_id = #{dailyArticleId}
AND member_id = #{memberId}
