mapper.update:
UPDATE HEART
SET heart_count = heart_count + 1
WHERE d_article_id = #{dailyArticleId}
  AND member_id = #{memberId}

UPDATE HEART
SET heart_count = heart_count - 1
WHERE d_article_id = #{dailyArticleId}
  AND member_id = #{memberId}

mapper.select:
SELECT count(*) "heartCount"
FROM heart
WHERE d_article_id = #{dailyArticleId}
  AND member_id = #{memberId}
