mapper.select:
SELECT title, enter, category_id, place_id, hitcount, regdate, time, member_id FROM (SELECT CEIL(rownum / #{searchDto.limit})page, title, enter, category_id, place_id, hitcount, TO_CHAR(regdate, 'YYYY-MM-DD HH24:MI') regdate, time, member_id FROM (SELECT title, enter, category_id, place_id, hitcount, regdate, time, member_id FROM meet_article
category_id = #{categoryId} AND title LIKE '%' || #{searchDto.title} || '%'
ORDER BY m_article_id DESC)) WHERE page = #{searchDto.page}

mapper.update:
UPDATE meet_article
SET hitcount = hitcount + 1
WHERE m_article_id = #{meetArticleId}

    SELECT hitcount FROM meet_article
    WHERE m_article_id = #{meetArticleId}

mapper.select:
SELECT COUNT(*)
FROM meet_article
WHERE category_id = #{categoryId}

mapper.delete:
DELETE FROM enter
       WHERE category_id = #{categoryId}
         AND m_article_id = #{meetArticleId}
         AND member_id = #{memberId}

mapper.select:
select join_member_id
FROM meet_room
WHERE meet_room_id = #{meetArticleId}

mapper.delete:
DELETE FROM meet_room
WHERE meet_room_id = #{meetRoomId}
  AND join_member_id = #{joinMemberId}
