mapper.update:
UPDATE meet_article
SET title   = #{title},
    content = #{content}
WHERE m_article_id = #{meetArticleId}

UPDATE meet_article
SET hitcount = hitcount + 1
WHERE m_article_id = #{meetArticleId}

    SELECT hitcount FROM meet_article
    WHERE m_article_id = #{meetArticleId}

mapper.select:
SELECT COUNT(*)
FROM meet_article
WHERE category_id = #{categoryId}
