mapper.update:
UPDATE member
SET email     = #{email},
    hobby     = #{hobby},
    interest  = #{interest},
    introduce = #{introduce}
WHERE member_id = #{memberId}

UPDATE member
SET nickname = #{nickname}
WHERE member_id = #{memberId}

UPDATE member
SET member_passwd = #{memberPasswd}
WHERE member_id = #{memberId}

UPDATE member
SET picture       = #{picture},
    store_picture = #{storePicture}
WHERE member_id = #{memberId}

mapper.select:
SELECT member_id
FROM member
WHERE name = #{name}
  AND nickname = #{nickname}

SELECT member_id, member_passwd, name, nickname, email
FROM member
WHERE member_id = #{id}
  AND name = #{name}
  AND email = #{email}

SELECT member_passwd
FROM member
WHERE member_id = #{memberId}

mapper.update:
UPDATE member
SET grade = 'BAN'
WHERE member_id = #{value}

UPDATE member
SET grade = 'GENERAL'
WHERE member_id = #{value}
